glam.Line.parse = function(docelt, geometry, material) {

	var verts = docelt.getElementsByTagName('vertices');
	if (verts) {
		verts = verts[0];
		glam.Types.parseVector3Array(verts, geometry.vertices);
	}
	
	var vertexColors = [];
	var colors = docelt.getElementsByTagName('colors');
	if (colors) {
		colors = colors[0];
		if (colors) {
			glam.Types.parseColor3Array(colors, vertexColors);
	
			var i, len = vertexColors.length;
	
			for (i = 0; i < len; i++) {			
				var c = vertexColors[i];
				geometry.colors.push(c.clone());
			}
	
			material.vertexColors = THREE.VertexColors;
		}
	}


}

/**
 * @fileoverview material parser/implementation
 * 
 * @author Tony Parisi
 */

glam.Material = {};

glam.Material.create = function(style, createCB, objtype) {
	var material = null;
	
	if (style) {
		var param = glam.Material.parseStyle(style);
		if (style.shader) {
			switch (style.shader.toLowerCase()) {
				case "phong" :
				case "blinn" :
					material = new THREE.MeshPhongMaterial(param);
					break;
				case "lambert" :
					material = new THREE.MeshLambertMaterial(param);
					break;
				case "line" :
					material = new THREE.LineBasicMaterial(param);
					break;
				case "basic" :
				default :
					material = new THREE.MeshBasicMaterial(param);
					break;
			}
		}
		else if (style["shader-vertex"] && style["shader-fragment"] && style["shader-uniforms"]) {
			material = glam.Material.createShaderMaterial(style, param, createCB);
		}
		else if (objtype == "line") {
			if (param.dashSize !== undefined  || param.gapSize !== undefined) {
				material = new THREE.LineDashedMaterial(param);
			}
			else {
				material = new THREE.LineBasicMaterial(param);
			}
		}
		else {
			material = new THREE.MeshBasicMaterial(param);
		}
	}
	else {
		material = new THREE.MeshBasicMaterial();
	}
	
	return material;
}

glam.Material.parseStyle = function(style) {
	var image = "";
	if (style.image) {
		image = glam.Material.parseUrl(style.image);
	}
	
	var reflectivity;
	if (style.reflectivity)
		reflectivity = parseFloat(style.reflectivity);
	
	var refractionRatio;
	if (style.refractionRatio)
		refractionRatio = parseFloat(style.refractionRatio);
	
	var envMap = glam.Material.tryParseEnvMap(style);
	
	var color;
	var diffuse;
	var specular;
	var css = "";

	if (css = style["color"]) {
		color = new THREE.Color().setStyle(css).getHex();
	}
	if (css = style["color-diffuse"]) {
		diffuse = new THREE.Color().setStyle(css).getHex();
	}
	if (css = style["color-specular"]) {
		specular = new THREE.Color().setStyle(css).getHex();
	}
	
	var opacity;
	if (style.opacity)
		opacity = parseFloat(style.opacity);

	var side = THREE.DoubleSide;
	if (style["backface-visibility"]) {
		switch (style["backface-visibility"].toLowerCase()) {
			case "visible" :
				side = THREE.DoubleSide;
				break;
			case "hidden" :
				side = THREE.FrontSide;
				break;
		}
	}
	
	var wireframe;
	if (style.hasOwnProperty("render-mode"))
		wireframe = (style["render-mode"] == "wireframe");
	
	var linewidth;
	if (style["line-width"]) {
		linewidth = parseInt(style["line-width"]);
	}
	
	var dashSize;
	if (style["dash-size"]) {
		dashSize = parseInt(style["dash-size"]);
	}

	var gapSize;
	if (style["gap-size"]) {
		gapSize = parseInt(style["gap-size"]);
	}
	
	var param = {
	};
	
	if (image)
		param.map = THREE.ImageUtils.loadTexture(image);
	if (envMap)
		param.envMap = envMap;
	if (color !== undefined)
		param.color = color;
	if (diffuse !== undefined)
		param.color = diffuse;
	if (specular !== undefined)
		param.specular = specular;
	if (opacity !== undefined) {
		param.opacity = opacity;
		param.transparent = opacity < 1;
	}
	if (wireframe !== undefined) {
		param.wireframe = wireframe;
	}
	if (linewidth !== undefined) {
		param.linewidth = linewidth;
	}
	if (dashSize !== undefined) {
		param.dashSize = dashSize;
	}
	if (gapSize !== undefined) {
		param.gapSize = gapSize;
	}
	if (reflectivity !== undefined)
		param.reflectivity = reflectivity;
	if (refractionRatio !== undefined)
		param.refractionRatio = refractionRatio;

	param.side = side;
	
	return param;
}

glam.Material.parseUrl = function(image) {
	var regExp = /\(([^)]+)\)/;
	var matches = regExp.exec(image);
	image = matches[1];
	return image;
}

glam.Material.tryParseEnvMap = function(style) {
	var urls = [];
	
	if (style["envmap-right"])
		urls.push(glam.Material.parseUrl(style["envmap-right"]));
	if (style["envmap-left"])
		urls.push(glam.Material.parseUrl(style["envmap-left"]));
	if (style["envmap-top"])
		urls.push(glam.Material.parseUrl(style["envmap-top"]));
	if (style["envmap-bottom"])
		urls.push(glam.Material.parseUrl(style["envmap-bottom"]));
	if (style["envmap-front"])
		urls.push(glam.Material.parseUrl(style["envmap-front"]));
	if (style["envmap-back"])
		urls.push(glam.Material.parseUrl(style["envmap-back"]));
	
	if (urls.length == 6) {
		var cubeTexture = THREE.ImageUtils.loadTextureCube( urls );
		return cubeTexture;
	}
	
	if (style["envmap"])
		return THREE.ImageUtils.loadTexture(glam.Material.parseUrl(style["envmap"]), THREE.SphericalRefractionMapping);
	
	return null;
}

glam.Material.createShaderMaterial = function(style, param, createCB) {
	
	function done() {
		var material = new THREE.ShaderMaterial({
			vertexShader : vstext,
			fragmentShader : fstext,
			uniforms: uniforms,
		});
		
		glam.Material.saveShaderMaterial(vsurl, fsurl, material);
		glam.Material.callShaderMaterialCallbacks(vsurl, fsurl);
	}
	
	var vs = style["shader-vertex"];
	var fs = style["shader-fragment"];
	var uniforms = glam.Material.parseUniforms(style["shader-uniforms"], param);

	var vsurl = glam.Material.parseUrl(vs);
	var fsurl = glam.Material.parseUrl(fs);

	if (!vsurl || !fsurl) {
		var vselt = document.getElementById(vs);
		var vstext = vselt.textContent;
		var fselt = document.getElementById(fs);
		var fstext = fselt.textContent;
		
		if (vstext && fstext) {
			return new THREE.ShaderMaterial({
				vertexShader : vstext,
				fragmentShader : fstext,
				uniforms: uniforms,
			});
		}
		else {
			return null;
		}
	}	
	
	var material = glam.Material.getShaderMaterial(vsurl, fsurl);
	if (material)
		return material;
	
	glam.Material.addShaderMaterialCallback(vsurl, fsurl, createCB);
	
	if (glam.Material.getShaderMaterialLoading(vsurl, fsurl))
		return;
	
	glam.Material.setShaderMaterialLoading(vsurl, fsurl);
	
	var vstext = "";
	var fstext = "";
	
	$.ajax({
	      type: 'GET',
	      url: vsurl,
	      dataType: "text",
	      success: function(result) { vstext = result; if (fstext) done(); },
	});	
	
	
	$.ajax({
	      type: 'GET',
	      url: fsurl,
	      dataType: "text",
	      success: function(result) { fstext = result; if (vstext) done(); },
	});	
}

glam.Material.parseUniforms = function(uniformsText, param) {
	
	var uniforms = {
	};
	
	var tokens = uniformsText.split(" ");

	var i, len = tokens.length / 3;
	for (i = 0; i < len; i++) {
		var name = tokens[i * 3];
		var type = tokens[i * 3 + 1];
		var value = tokens[i * 3 + 2];
		
		if (type == "f")
			value = parseFloat(value);
		else if (type == "t") {
			value = value.toLowerCase();
			if (value == "envmap") {
				value = param.envMap;
			}
			else if (value == "texture" || value == "map") {
				value = param.map;
			}
		}
		
		var uniform =  {
			type : type,
			value : value,
		};
		
		uniforms[name] = uniform;
	}
		
	return uniforms;
}

glam.Material.shaderMaterials = {};

glam.Material.saveShaderMaterial = function(vsurl, fsurl, material) {
	var key = vsurl + fsurl;
	var entry = glam.Material.shaderMaterials[key];
	entry.material = material;
	entry.loading = false;
}

glam.Material.addShaderMaterialCallback = function(vsurl, fsurl, cb) {
	var key = vsurl + fsurl;
	
	var entry = glam.Material.shaderMaterials[key];
	if (!entry) {
		glam.Material.shaderMaterials[key] = {
			material : null,
			loading : false,
			callbacks : [],
		};
	}
	
	glam.Material.shaderMaterials[key].callbacks.push(cb);
}

glam.Material.callShaderMaterialCallbacks = function(vsurl, fsurl) {
	var key = vsurl + fsurl;
	
	var entry = glam.Material.shaderMaterials[key];
	if (entry && entry.material) {
		for (cb in entry.callbacks) {
			entry.callbacks[cb](entry.material);
		}
	}
}

glam.Material.getShaderMaterial = function(vsurl, fsurl) {
	
	var key = vsurl + fsurl;
	var entry = glam.Material.shaderMaterials[key];
	if (entry) {
		return entry.material;
	}
	else {
		return null;
	}
}

glam.Material.setShaderMaterialLoading = function(vsurl, fsurl) {
	
	var key = vsurl + fsurl;
	var entry = glam.Material.shaderMaterials[key];
	if (entry) {
		entry.loading = true;
	}
}

glam.Material.getShaderMaterialLoading = function(vsurl, fsurl) {
	
	var key = vsurl + fsurl;
	var entry = glam.Material.shaderMaterials[key];
	return (entry && entry.loading);
}

glam.Material.addHandlers = function(docelt, style, obj) {

	docelt.glam.setAttributeHandlers.push(function(attr, val) {
		glam.Material.onSetAttribute(obj, docelt, attr, val);
	});
	
	style.setPropertyHandlers.push(function(attr, val) {
		glam.Material.onSetProperty(obj, docelt, attr, val);
	});
}

glam.Material.onSetAttribute = function(obj, docelt, attr, val) {

	var material = obj.visuals[0].material;
	switch (attr) {
		case "color" :
		case "color-diffuse" :
		case "colorDiffuse" :
			material.color.setStyle(val);
			break;
	}
}

glam.Material.onSetProperty = function(obj, docelt, attr, val) {

	var material = obj.visuals[0].material;
	switch (attr) {
		case "color" :
		case "color-diffuse" :
		case "colorDiffuse" :
			material.color.setStyle(val);
			break;
	}
}
/**
 * @fileoverview mesh parser/implementation. currently only supports triangle sets
 * 
 * @author Tony Parisi
 */

glam.Mesh = {};
glam.Mesh.VERTEX_NORMALS = false;
glam.Mesh.VERTEX_COLORS = false;

glam.Mesh.create = function(docelt, style) {
	
	return glam.Visual.create(docelt, style, glam.Mesh);
}

glam.Mesh.getAttributes = function(docelt, style, param) {
	
	var vertexNormals = docelt.getAttribute('vertexNormals');
	if (vertexNormals !== null) {
		vertexNormals = true;
	}
	else {
		vertexNormals = glam.Mesh.VERTEX_NORMALS;
	}
	
	var vertexColors = docelt.getAttribute('vertexColors');
	if (vertexColors !== null) {
		vertexColors = true;
	}
	else {
		vertexColors = glam.Mesh.VERTEX_COLORS;
	}
	
	if (style) {
		if (style.vertexNormals)
			vertexNormals = style.vertexNormals;
		if (style.vertexColors)
			vertexColors = style.vertexColors;
	}
	
	param.vertexNormals = vertexNormals;
	param.vertexColors = vertexColors;
}

glam.Mesh.createVisual = function(docelt, material, param) {

	var geometry = new THREE.Geometry;
	
	glam.Mesh.parse(docelt, geometry, material, param);
	
	var mesh = new THREE.Mesh(geometry, material);
	var visual = new Vizi.Visual(
			{
				object : mesh,
			});
	
	return visual;
}

glam.Mesh.parse = function(docelt, geometry, material, param) {

	var verts = docelt.getElementsByTagName('vertices');
	if (verts) {
		verts = verts[0];
		if (verts) {
			glam.Types.parseVector3Array(verts, geometry.vertices);
		}
	}
	
	var uvs = docelt.getElementsByTagName('uvs');
	if (uvs) {
		uvs = uvs[0];
		if (uvs) {
			glam.Types.parseUVArray(uvs, geometry.faceVertexUvs[0]);
		}
	}

	var faces = docelt.getElementsByTagName('faces');
	if (faces) {
		faces = faces[0];
		if (faces) {
			glam.Types.parseFaceArray(faces, geometry.faces);
		}
	}

	var vertexNormals = [];
	var normals = docelt.getElementsByTagName('normals');
	if (normals) {
		normals = normals[0];
		if (normals) {
			glam.Types.parseVector3Array(normals, vertexNormals);
			
			if (param.vertexNormals) {
				
				var i, len = geometry.faces.length;
	
				for (i = 0; i < len; i++) {
					
					var face = geometry.faces[i];
					if (face) {
						var norm = vertexNormals[face.a].normalize().clone();
						face.vertexNormals[0] = norm;
						var norm = vertexNormals[face.b].normalize().clone();
						face.vertexNormals[1] = norm;
						var norm = vertexNormals[face.c].normalize().clone();
						face.vertexNormals[2] = norm;
					}
				}
			}
			else {
				
				var i, len = geometry.faces.length;
	
				for (i = 0; i < len; i++) {
					
					var face = geometry.faces[i];
					if (face) {
						var norm = vertexNormals[i].normalize();
						face.normal.copy(norm);
					}
				}
			}
		}
	}
	
	var vertexColors = [];
	var colors = docelt.getElementsByTagName('colors');
	if (colors) {
		colors = colors[0];
		if (colors) {
			glam.Types.parseColor3Array(colors, vertexColors);
	
			if (param.vertexColors) {
	
				var i, len = geometry.faces.length;
	
				for (i = 0; i < len; i++) {
					
					var face = geometry.faces[i];
					if (face) {
						var c = vertexColors[face.a];
						if (c) {
							face.vertexColors[0] = c.clone();
						}
						var c = vertexColors[face.b];
						if (c) {
							face.vertexColors[1] = c.clone();
						}
						var c = vertexColors[face.c];
						if (c) {
							face.vertexColors[2] = c.clone();
						}
					}
				}
	
				material.vertexColors = THREE.VertexColors;
			}
			else {
				
				var i, len = geometry.faces.length;
	
				for (i = 0; i < len; i++) {
					
					var face = geometry.faces[i];
					if (face) {
						var c = vertexColors[i];
						if (c) {
							face.color.copy(c);
						}
					}
				}
				
				material.vertexColors = THREE.FaceColors; 
			}
		
			geometry.colorsNeedUpdate = true;
			geometry.buffersNeedUpdate = true;
		}
	}
}

